      next: () => {
        this.loadPositions();
        this.positionForm.reset();
        this.showPositionForm = false;
        this.showMainInterface = true;
        this.errorMessage = '';
        this.isLoading = false;
        this.modalService.showAlert(`${this.translationService.translate('SUCCESS.MESSAGE')}`, this.translationService.translate('ADMIN.POSITION_CREATED_SUCCESS')).pipe(untilDestroyed(this)).subscribe();
      },
      error: error => {
        console.error(this.translationService.translate('CONSOLE.ERROR_CREATING_POSITION'), error);
        this.errorMessage = error.message;
        this.isLoading = false;
      }
    });
  }
  deletePosition(id) {
