import { Component, OnInit } from '@angular/core';
import { EmployeeService } from '../../services/employee.service';
import { AuthService } from '../../services/auth.service';
import { TranslationService } from '../../services/translation.service';
import { IdGeneratorService } from '../../services/id-generator.service';
import { Router } from '@angular/router';
import { IEmployee, IPosition } from '../../interfaces/employee.interface';

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.scss']
})
export class AdminDashboardComponent implements OnInit {
  employees: IEmployee[] = [];
  positions: IPosition[] = [];
  selectedEmployee: IEmployee | null = null;
  isEditing = false;
  showEmployeeForm = false;
  showPositionForm = false;
  newPositionName = '';

  constructor(
    private employeeService: EmployeeService,
    private authService: AuthService,
    public translationService: TranslationService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadEmployees();
    this.loadPositions();
  }

  loadEmployees(): void {
    this.employeeService.getEmployees().subscribe({
      next: (employees) => this.employees = employees,
      error: (error) => console.error('Error loading employees:', error)
    });
  }

  loadPositions(): void {
    this.employeeService.getPositions().subscribe({
      next: (positions) => this.positions = positions,
      error: (error) => console.error('Error loading positions:', error)
    });
  }

  createEmployee(): void {
    this.selectedEmployee = {
      employee_id: '',
      first_name: '',
      last_name: '',
      middle_name: '',
      position_id: 0,
      address: '',
      contact_email: '',
      contact_phone: '',
      salary: 0
    };
    this.isEditing = false;
    this.showEmployeeForm = true;
  }

  editEmployee(employee: IEmployee): void {
    this.selectedEmployee = { ...employee };
    this.isEditing = true;
    this.showEmployeeForm = true;
  }

  deleteEmployee(id: number): void {
    if (confirm(this.translationService.translate('ACTIONS.CONFIRM_DELETE'))) {
      this.employeeService.deleteEmployee(id).subscribe({
        next: () => this.loadEmployees(),
        error: (error) => console.error('Error deleting employee:', error)
      });
    }
  }

  saveEmployee(employee: IEmployee): void {
    if (this.isEditing && employee.id) {
      this.employeeService.updateEmployee(employee.id, employee).subscribe({
        next: () => {
          this.loadEmployees();
          this.showEmployeeForm = false;
        },
        error: (error) => console.error('Error updating employee:', error)
      });
    } else {
      this.employeeService.createEmployee(employee).subscribe({
        next: () => {
          this.loadEmployees();
          this.showEmployeeForm = false;
        },
        error: (error) => console.error('Error creating employee:', error)
      });
    }
  }

  createPosition(): void {
    if (this.newPositionName.trim()) {
      const position: IPosition = { name: this.newPositionName.trim() };
      this.employeeService.createPosition(position).subscribe({
        next: () => {
          this.loadPositions();
          this.newPositionName = '';
          this.showPositionForm = false;
        },
        error: (error) => console.error('Error creating position:', error)
      });
    }
  }

  deletePosition(id: number): void {
    if (confirm(this.translationService.translate('ACTIONS.CONFIRM_DELETE'))) {
      this.employeeService.deletePosition(id).subscribe({
        next: () => this.loadPositions(),
        error: (error) => console.error('Error deleting position:', error)
      });
    }
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/employees']);
  }
}
