import { Component, OnInit } from '@angular/core';
import { EmployeeService } from '../../services/employee.service';
import { TranslationService } from '../../services/translation.service';
import { AuthService } from '../../services/auth.service';
import { IEmployee } from '../../interfaces/employee.interface';

@Component({
  selector: 'app-employee-list',
  templateUrl: './employee-list.component.html',
  styleUrls: ['./employee-list.component.scss']
})
export class EmployeeListComponent implements OnInit {
  employees: IEmployee[] = [];
  filteredEmployees: IEmployee[] = [];
  sortBy: string = 'last_name';
  filterPosition: string = '';
  isAdmin: boolean = false;

  constructor(
    private employeeService: EmployeeService,
    private authService: AuthService,
    public translationService: TranslationService // ДОБАВИТЬ КАК PUBLIC
  ) {}

  ngOnInit(): void {
    // Проверяем является ли пользователь администратором
    this.authService.isLoggedIn().subscribe(isLoggedIn => {
      this.isAdmin = isLoggedIn;
    });
    
    this.loadEmployees();
  }

  loadEmployees(): void {
    this.employeeService.getEmployees().subscribe({
      next: (employees) => {
        this.employees = employees;
        this.applyFilters();
      },
      error: (error) => console.error('Error loading employees:', error)
    });
  }

  applyFilters(): void {
    let filtered = [...this.employees];

    // Фильтрация по должности (выпадающий список)
    if (this.filterPosition && this.filterPosition !== 'all') {
      filtered = filtered.filter(emp => 
        emp.position_id === parseInt(this.filterPosition)
      );
    }

    // Сортировка
    filtered.sort((a, b) => {
      switch (this.sortBy) {
        case 'last_name':
          return a.last_name.localeCompare(b.last_name);
        case 'position':
          return (a.position_name || '').localeCompare(b.position_name || '');
        default:
          return 0;
      }
    });

    this.filteredEmployees = filtered;
  }

  onSortChange(sortBy: string): void {
    this.sortBy = sortBy;
    this.applyFilters();
  }

  onFilterChange(event: any): void {
    this.filterPosition = event.target.value;
    this.applyFilters();
  }


  
  onPositionFilterChange(positionId: string): void {
    this.filterPosition = positionId;
    this.applyFilters();
  }

  // Сброс фильтров
  resetFilters(): void {
    this.filterPosition = '';
    this.sortBy = 'last_name';
    this.applyFilters();
  }

  // Получить количество сотрудников по должности
  getEmployeesCountByPosition(positionId: number): number {
    return this.employees.filter(emp => emp.position_id === positionId).length;
  }
}
