let AdminDashboardComponent = class AdminDashboardComponent {
  constructor(employeeService, authService, translationService, idGenerator, router, modalService, fb) {
    this.employeeService = employeeService;
    this.authService = authService;
    this.translationService = translationService;
    this.idGenerator = idGenerator;
    this.router = router;
    this.modalService = modalService;
    this.fb = fb;
    this.employees = [];
    this.positions = [];
    this.selectedEmployee = null;
    this.isEditing = false;
    // Состояния интерфейса
    this.showEmployeeForm = false;
    this.showPositionForm = false;
    this.showMainInterface = true;
    this.newPositionName = '';
    this.errorMessage = '';
    this.isLoading = false;
    // Паттерны для валидации
    this.phonePattern = /^(\d{6}|\+\d{1,3}\(\d{1,4}\)\d{7})$/;
    this.namePattern = /^[a-zA-Zа-яА-ЯёЁ\s\-]+$/;
    // Инициализация формы сотрудника
    this.employeeForm = this.fb.group({
      first_name: ['', [Validators.required, Validators.minLength(2), Validators.maxLength(50), this.nameValidator.bind(this)]],
      last_name: ['', [Validators.required, Validators.minLength(2), Validators.maxLength(50), this.nameValidator.bind(this)]],
      middle_name: ['', [Validators.maxLength(50), this.nameValidator.bind(this)]],
      position_id: ['', Validators.required],
      address: ['', Validators.maxLength(200)],
      contact_email: ['', [Validators.required, Validators.email]],
      contact_phone: ['', [Validators.required, this.phoneValidator.bind(this)]],
      salary: [0, [Validators.required, Validators.min(0), Validators.max(1000000)]]
    });
    // Инициализация формы должности
    this.positionForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(2), Validators.maxLength(50), this.nameValidator.bind(this)]]
    });
  }
  ngOnInit() {
    // Проверяем авторизацию при загрузке
    if (!this.authService.validateToken()) {
      this.router.navigate(['/login']);
      return;
    }
    this.loadEmployees();
    this.loadPositions();
  }
  // Кастомные валидаторы
  phoneValidator(control) {
    if (!control.value) {
      return null;
    }
    const value = control.value.toString().trim();
    // Проверяем общий паттерн
    if (!this.phonePattern.test(value)) {
      return {
        phoneFormat: true
      };
    }
    // Дополнительные проверки для разных форматов
    if (value.includes('+')) {
      // Форматы с кодом страны: +375(11)1111111 и +7(111)1111111
      const countryCodeMatch = value.match(/^\+\d{1,3}/);
      if (!countryCodeMatch) {
        return {
          phoneFormat: true
        };
      }
      const countryCode = countryCodeMatch[0];
      const restOfNumber = value.slice(countryCode.length);
      // Проверяем формат после кода страны
      const restPattern = /^\(\d{1,4}\)\d{7}$/;
      if (!restPattern.test(restOfNumber)) {
        return {
          phoneFormat: true
        };
      }
      // Проверяем конкретные форматы
      if (countryCode === '+375') {
        // +375(XX)XXXXXXX - код оператора 2 цифры, номер 7 цифр
        const operatorCode = restOfNumber.match(/\((\d{2})\)/);
        if (!operatorCode || operatorCode[1].length !== 2) {
          return {
            phoneFormat: true
          };
        }
      } else if (countryCode === '+7') {
        // +7(XXX)XXXXXXX - код региона 3 цифры, номер 7 цифр
        const regionCode = restOfNumber.match(/\((\d{3})\)/);
        if (!regionCode || regionCode[1].length !== 3) {
          return {
            phoneFormat: true
          };
        }
      } else {
        // Для других кодов стран проверяем общую структуру
        const operatorCode = restOfNumber.match(/\((\d+)\)/);
        if (!operatorCode || operatorCode[1].length < 1 || operatorCode[1].length > 4) {
          return {
            phoneFormat: true
          };
        }
      }
    } else {
      // Простой формат: 111111 (ровно 6 цифр)
      if (!/^\d{6}$/.test(value)) {
        return {
          phoneFormat: true
        };
      }
    }
    return null;
  }
  nameValidator(control) {
    if (!control.value) {
      return null;
    }
    const value = control.value.toString().trim();
    // Проверяем, что имя содержит только буквы, пробелы и дефисы
    if (!this.namePattern.test(value)) {
      return {
        invalidName: true
      };
    }
    return null;
  }
  loadEmployees() {
    this.isLoading = true;
    this.employeeService.getEmployees().pipe(untilDestroyed(this)).subscribe({
      next: employees => {
        this.employees = employees;
        this.isLoading = false;
      },
      error: error => {
        console.error('Error loading employees:', error);
        this.errorMessage = error.message;
        this.isLoading = false;
        // Если ошибка авторизации, перенаправляем на логин
        if (error.message.includes('Неавторизованный')) {
          this.router.navigate(['/login']);
        }
      }
    });
  }
  loadPositions() {
    this.employeeService.getPositions().pipe(untilDestroyed(this)).subscribe({
      next: positions => {
        this.positions = positions;
      },
      error: error => {
        console.error('Error loading positions:', error);
        this.errorMessage = error.message;
        if (error.message.includes('Неавторизованный')) {
          this.router.navigate(['/login']);
        }
      }
    });
  }
  // Управление интерфейсом
  showAddEmployeeForm() {
    this.showMainInterface = false;
    this.showEmployeeForm = true;
    this.showPositionForm = false;
    this.employeeForm.reset({
      first_name: '',
      last_name: '',
      middle_name: '',
      position_id: this.positions.length > 0 ? this.positions[0].id : '',
      address: '',
      contact_email: '',
      contact_phone: '',
      salary: 0
    });
    this.selectedEmployee = null;
    this.isEditing = false;
    this.errorMessage = '';
  }
  showAddPositionForm() {
    this.showMainInterface = false;
    this.showPositionForm = true;
    this.showEmployeeForm = false;
    this.positionForm.reset({
      name: ''
    });
    this.errorMessage = '';
  }
  cancelForm() {
    this.showMainInterface = true;
    this.showEmployeeForm = false;
    this.showPositionForm = false;
    this.selectedEmployee = null;
    this.errorMessage = '';
    this.employeeForm.reset();
    this.positionForm.reset();
  }
  createEmployee() {
    if (!this.authService.validateToken()) {
      this.router.navigate(['/login']);
      return;
    }
    this.showAddEmployeeForm();
  }
  editEmployee(employee) {
    this.selectedEmployee = {
      ...employee
    };
    this.isEditing = true;
    this.showEmployeeForm = true;
    this.showMainInterface = false;
    this.showPositionForm = false;
    // Заполняем форму данными сотрудника
    this.employeeForm.patchValue({
      first_name: employee.first_name,
      last_name: employee.last_name,
      middle_name: employee.middle_name || '',
      position_id: employee.position_id,
      address: employee.address || '',
      contact_email: employee.contact_email,
      contact_phone: employee.contact_phone,
      salary: employee.salary
    });
  }
  deleteEmployee(id) {
    const employee = this.employees.find(emp => emp.id === id);
    if (!employee) return;
    const message = `<strong>Вы уверены, что хотите удалить сотрудника из базы данных?</strong>
    <br><br>
    <strong>ID:</strong> ${employee.employee_id}<br>
    <strong>ФИО:</strong> ${employee.last_name} ${employee.first_name} ${employee.middle_name || ''}<br>
    <strong>Должность:</strong> ${employee.position_name}
  `;
    this.modalService.showConfirm('Удаление сотрудника', message, 'Удалить', 'Отмена').pipe(untilDestroyed(this)).subscribe(confirmed => {
      if (confirmed) {
        this.employeeService.deleteEmployee(id).pipe(untilDestroyed(this)).subscribe({
          next: () => {
            this.loadEmployees();
            this.errorMessage = '';
          },
          error: error => {
            console.error('Error deleting employee:', error);
            this.errorMessage = error.message;
          }
        });
      }
    });
  }
  saveEmployee() {
    if (!this.authService.validateToken()) {
      this.router.navigate(['/login']);
      return;
    }
    // Проверяем валидность формы
    if (this.employeeForm.invalid) {
      this.markFormGroupTouched(this.employeeForm);
      return;
    }
    const formValue = this.employeeForm.value;
    const employee = {
      ...formValue,
      id: this.isEditing && this.selectedEmployee ? this.selectedEmployee.id : undefined
    };
    // Генерируем уникальный ID только для новых сотрудников
    if (!this.isEditing) {
      const generatedId = this.idGenerator.generateEmployeeId(employee.first_name, employee.last_name, employee.middle_name);
      employee.employee_id = generatedId;
      console.log('Сгенерированный ID сотрудника:', generatedId);
    }
    this.isLoading = true;
    if (this.isEditing && employee.id) {
      this.employeeService.updateEmployee(employee.id, employee).pipe(untilDestroyed(this)).subscribe({
        next: () => {
          this.loadEmployees();
          this.showEmployeeForm = false;
          this.showMainInterface = true;
          this.errorMessage = '';
          this.isLoading = false;
          this.modalService.showAlert('Успех', 'Сотрудник успешно обновлен!').pipe(untilDestroyed(this)).subscribe();
        },
        error: error => {
          console.error('Ошибка при изменении данных сотрудника:', error);
          this.errorMessage = error.message;
          this.isLoading = false;
        }
      });
    } else {
      this.employeeService.createEmployee(employee).pipe(untilDestroyed(this)).subscribe({
        next: () => {
          this.loadEmployees();
          this.showEmployeeForm = false;
          this.showMainInterface = true;
          this.errorMessage = '';
          this.isLoading = false;
          this.modalService.showAlert('Успех', 'Сотрудник успешно создан!').pipe(untilDestroyed(this)).subscribe();
        },
        error: error => {
          console.error('Ошибка создания сотрудника:', error);
          this.errorMessage = error.message;
          this.isLoading = false;
        }
      });
    }
  }
  createPosition() {
    if (!this.authService.validateToken()) {
      this.router.navigate(['/login']);
      return;
    }
    // Проверяем валидность формы
    if (this.positionForm.invalid) {
      this.markFormGroupTouched(this.positionForm);
      return;
    }
    const formValue = this.positionForm.value;
    this.isLoading = true;
    const position = {
      name: formValue.name.trim()
    };
    this.employeeService.createPosition(position).pipe(untilDestroyed(this)).subscribe({
      next: () => {
        this.loadPositions();
        this.positionForm.reset();
        this.showPositionForm = false;
        this.showMainInterface = true;
        this.errorMessage = '';
        this.isLoading = false;
