    this.modalService.showConfirm('Удаление должности', message, 'Удалить', 'Отмена').pipe(untilDestroyed(this)).subscribe(confirmed => {
      if (confirmed) {
        this.employeeService.deletePosition(id).pipe(untilDestroyed(this)).subscribe({
          next: () => {
            this.loadPositions();
            this.errorMessage = '';
          },
          error: error => {
            console.error('Error deleting position:', error);
            this.errorMessage = error.message;
          }
        });
      }
    });
  }
  logout() {
    this.authService.logout();
    this.router.navigate(['/employees']);
  }
