import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, AbstractControl, ValidationErrors } from '@angular/forms';
import { EmployeeService } from '../../shared/services/employee.service';
import { AuthService } from '../../shared/services/auth.service';
import { TranslationService } from '../../shared/services/translation.service';
import { IdGeneratorService } from '../../shared/services/id-generator.service';
import { Router } from '@angular/router';
import { IEmployee, IPosition } from '../../shared/interfaces/employee.interface';

import { ModalService } from '../../shared/services/modal.service';
import { UntilDestroy, untilDestroyed } from '@ngneat/until-destroy';

@UntilDestroy()
@Component({
    selector: 'app-admin-dashboard',
    templateUrl: './admin-dashboard.component.html',
    styleUrls: ['./admin-dashboard.component.scss']
})
export class AdminDashboardComponent implements OnInit {
    employees: IEmployee[] = [];
    positions: IPosition[] = [];
    selectedEmployee: IEmployee | null = null;
    isEditing = false;

    // Состояния интерфейса
    showEmployeeForm = false;
    showPositionForm = false;
    showMainInterface = true;

    // Реактивные формы
    employeeForm: FormGroup;
    positionForm: FormGroup;

    newPositionName = '';
    errorMessage = '';
    isLoading = false;

    // Паттерны для валидации
    private readonly phonePattern = /^(\d{6}|\+\d{1,3}\(\d{1,4}\)\d{7})$/;
    private readonly namePattern = /^[a-zA-Zа-яА-ЯёЁ\s\-]+$/;

    constructor(
        private employeeService: EmployeeService,
        private authService: AuthService,
        public translationService: TranslationService,
        private idGenerator: IdGeneratorService,
        private router: Router,
        private modalService: ModalService,
        private fb: FormBuilder
    ) {
        // Инициализация формы сотрудника
        this.employeeForm = this.fb.group({
            first_name: ['', [
                Validators.required, 
                Validators.minLength(2),
                Validators.maxLength(50),
                this.nameValidator.bind(this)
            ]],
            last_name: ['', [
                Validators.required, 
                Validators.minLength(2),
                Validators.maxLength(50),
                this.nameValidator.bind(this)
            ]],
            middle_name: ['', [
                Validators.maxLength(50),
                this.nameValidator.bind(this)
            ]],
            position_id: ['', Validators.required],
            address: ['', Validators.maxLength(200)],
            contact_email: ['', [Validators.required, Validators.email]],
            contact_phone: ['', [
                Validators.required,
                this.phoneValidator.bind(this)
            ]],
            salary: [0, [
                Validators.required, 
                Validators.min(0),
                Validators.max(1000000)
            ]]
        });

        // Инициализация формы должности
        this.positionForm = this.fb.group({
            name: ['', [
                Validators.required, 
                Validators.minLength(2),
                Validators.maxLength(50),
                this.nameValidator.bind(this)
            ]]
        });
    }

    ngOnInit(): void {
        // Проверяем авторизацию при загрузке
        if (!this.authService.validateToken()) {
            this.router.navigate(['/login']);
            return;
        }

        this.loadEmployees();
        this.loadPositions();
    }

    // Кастомные валидаторы
    private phoneValidator(control: AbstractControl): ValidationErrors | null {
        if (!control.value) {
            return null;
        }

        const value = control.value.toString().trim();
        
        // Проверяем общий паттерн
        if (!this.phonePattern.test(value)) {
            return { phoneFormat: true };
        }

        // Дополнительные проверки для разных форматов
        if (value.includes('+')) {
            // Форматы с кодом страны: +375(11)1111111 и +7(111)1111111
            const countryCodeMatch = value.match(/^\+\d{1,3}/);
            if (!countryCodeMatch) {
                return { phoneFormat: true };
            }

            const countryCode = countryCodeMatch[0];
            const restOfNumber = value.slice(countryCode.length);
            
            // Проверяем формат после кода страны
            const restPattern = /^\(\d{1,4}\)\d{7}$/;
            if (!restPattern.test(restOfNumber)) {
                return { phoneFormat: true };
            }

            // Проверяем конкретные форматы
            if (countryCode === '+375') {
                // +375(XX)XXXXXXX - код оператора 2 цифры, номер 7 цифр
                const operatorCode = restOfNumber.match(/\((\d{2})\)/);
                if (!operatorCode || operatorCode[1].length !== 2) {
                    return { phoneFormat: true };
                }
            } else if (countryCode === '+7') {
                // +7(XXX)XXXXXXX - код региона 3 цифры, номер 7 цифр
                const regionCode = restOfNumber.match(/\((\d{3})\)/);
                if (!regionCode || regionCode[1].length !== 3) {
                    return { phoneFormat: true };
                }
            } else {
                // Для других кодов стран проверяем общую структуру
                const operatorCode = restOfNumber.match(/\((\d+)\)/);
                if (!operatorCode || operatorCode[1].length < 1 || operatorCode[1].length > 4) {
                    return { phoneFormat: true };
                }
            }
        } else {
            // Простой формат: 111111 (ровно 6 цифр)
            if (!/^\d{6}$/.test(value)) {
                return { phoneFormat: true };
            }
        }

        return null;
    }

    private nameValidator(control: AbstractControl): ValidationErrors | null {
        if (!control.value) {
            return null;
        }

        const value = control.value.toString().trim();
        
        // Проверяем, что имя содержит только буквы, пробелы и дефисы
        if (!this.namePattern.test(value)) {
            return { invalidName: true };
        }

        return null;
    }

    public loadEmployees(): void {
        this.isLoading = true;
        this.employeeService.getEmployees().pipe(untilDestroyed(this)).subscribe({
            next: (employees) => {
                this.employees = employees;
                this.isLoading = false;
            },
            error: (error) => {
                console.error('Error loading employees:', error);
                this.errorMessage = error.message;
                this.isLoading = false;

                // Если ошибка авторизации, перенаправляем на логин
                if (error.message.includes('Неавторизованный')) {
                    this.router.navigate(['/login']);
                }
            }
        });
    }

    public loadPositions(): void {
        this.employeeService.getPositions().pipe(untilDestroyed(this)).subscribe({
            next: (positions) => {
                this.positions = positions;
            },
            error: (error) => {
                console.error('Error loading positions:', error);
                this.errorMessage = error.message;

                if (error.message.includes('Неавторизованный')) {
                    this.router.navigate(['/login']);
                }
            }
        });
    }

    // Управление интерфейсом
    public showAddEmployeeForm(): void {
        this.showMainInterface = false;
        this.showEmployeeForm = true;
        this.showPositionForm = false;

        this.employeeForm.reset({
            first_name: '',
            last_name: '',
            middle_name: '',
            position_id: this.positions.length > 0 ? this.positions[0].id! : '',
            address: '',
            contact_email: '',
            contact_phone: '',
            salary: 0
        });
        
        this.selectedEmployee = null;
        this.isEditing = false;
        this.errorMessage = '';
    }

    public showAddPositionForm(): void {
        this.showMainInterface = false;
        this.showPositionForm = true;
        this.showEmployeeForm = false;
        this.positionForm.reset({ name: '' });
        this.errorMessage = '';
    }

    public cancelForm(): void {
        this.showMainInterface = true;
        this.showEmployeeForm = false;
        this.showPositionForm = false;
        this.selectedEmployee = null;
        this.errorMessage = '';
        this.employeeForm.reset();
        this.positionForm.reset();
    }

    public createEmployee(): void {
        if (!this.authService.validateToken()) {
            this.router.navigate(['/login']);
            return;
        }

        this.showAddEmployeeForm();
    }

    public editEmployee(employee: IEmployee): void {
        this.selectedEmployee = { ...employee };
        this.isEditing = true;
        this.showEmployeeForm = true;
        this.showMainInterface = false;
        this.showPositionForm = false;

        // Заполняем форму данными сотрудника
        this.employeeForm.patchValue({
            first_name: employee.first_name,
            last_name: employee.last_name,
            middle_name: employee.middle_name || '',
            position_id: employee.position_id,
            address: employee.address || '',
            contact_email: employee.contact_email,
            contact_phone: employee.contact_phone,
            salary: employee.salary
        });
    }

    public deleteEmployee(id: number): void {
        const employee = this.employees.find(emp => emp.id === id);
        if (!employee) return;

        const message = `<strong>Вы уверены, что хотите удалить сотрудника из базы данных?</strong>
    <br><br>
    <strong>ID:</strong> ${employee.employee_id}<br>
    <strong>ФИО:</strong> ${employee.last_name} ${employee.first_name} ${employee.middle_name || ''}<br>
    <strong>Должность:</strong> ${employee.position_name}
  `;

        this.modalService.showConfirm('Удаление сотрудника', message, 'Удалить', 'Отмена')
            .pipe(untilDestroyed(this))
            .subscribe(confirmed => {
                if (confirmed) {
                    this.employeeService.deleteEmployee(id).pipe(untilDestroyed(this)).subscribe({
                        next: () => {
                            this.loadEmployees();
                            this.errorMessage = '';
                        },
                        error: (error) => {
                            console.error('Error deleting employee:', error);
                            this.errorMessage = error.message;
                        }
                    });
                }
            });
    }

    public saveEmployee(): void {
        if (!this.authService.validateToken()) {
            this.router.navigate(['/login']);
            return;
        }

        // Проверяем валидность формы
        if (this.employeeForm.invalid) {
            this.markFormGroupTouched(this.employeeForm);
            return;
        }

        const formValue = this.employeeForm.value;
        const employee: IEmployee = {
            ...formValue,
            id: this.isEditing && this.selectedEmployee ? this.selectedEmployee.id : undefined
        };

        // Генерируем уникальный ID только для новых сотрудников
        if (!this.isEditing) {
            const generatedId = this.idGenerator.generateEmployeeId(
                employee.first_name,
                employee.last_name,
                employee.middle_name
            );
            employee.employee_id = generatedId;
            console.log('Сгенерированный ID сотрудника:', generatedId);
        }

        this.isLoading = true;

        if (this.isEditing && employee.id) {
            this.employeeService.updateEmployee(employee.id, employee)
                .pipe(untilDestroyed(this)).subscribe({
                next: () => {
                    this.loadEmployees();
                    this.showEmployeeForm = false;
                    this.showMainInterface = true;
                    this.errorMessage = '';
                    this.isLoading = false;
                    this.modalService.showAlert('Успех', 'Сотрудник успешно обновлен!')
                        .pipe(untilDestroyed(this)).subscribe();
                },
                error: (error) => {
                    console.error('Ошибка при изменении данных сотрудника:', error);
                    this.errorMessage = error.message;
                    this.isLoading = false;
                }
            });
        } else {
            this.employeeService.createEmployee(employee)
                .pipe(untilDestroyed(this)).subscribe({
                next: () => {
                    this.loadEmployees();
                    this.showEmployeeForm = false;
                    this.showMainInterface = true;
                    this.errorMessage = '';
                    this.isLoading = false;
                    this.modalService.showAlert('Успех', 'Сотрудник успешно создан!')
                        .pipe(untilDestroyed(this)).subscribe();
                },
                error: (error) => {
                    console.error('Ошибка создания сотрудника:', error);
                    this.errorMessage = error.message;
                    this.isLoading = false;
                }
            });
        }
    }

    public createPosition(): void {
        if (!this.authService.validateToken()) {
            this.router.navigate(['/login']);
            return;
        }

        // Проверяем валидность формы
        if (this.positionForm.invalid) {
            this.markFormGroupTouched(this.positionForm);
            return;
        }

        const formValue = this.positionForm.value;
        
        this.isLoading = true;
        const position: IPosition = { name: formValue.name.trim() };

        this.employeeService.createPosition(position)
            .pipe(untilDestroyed(this)).subscribe({
            next: () => {
                this.loadPositions();
                this.positionForm.reset();
                this.showPositionForm = false;
                this.showMainInterface = true;
                this.errorMessage = '';
                this.isLoading = false;
                this.modalService.showAlert('Успех', 'Должность успешно добавлена!')
                    .pipe(untilDestroyed(this)).subscribe();
            },
            error: (error) => {
                console.error('Ошибка создания должности:', error);
                this.errorMessage = error.message;
                this.isLoading = false;
            }
        });
    }

    public deletePosition(id: number): void {
        const position = this.positions.find(pos => pos.id === id);
        if (!position) return;

        const employeesWithThisPosition = this.employees.filter(emp => emp.position_id === id);

        if (employeesWithThisPosition.length > 0) {
            let employeeList = '';
            employeesWithThisPosition.forEach(emp => {
                employeeList += `• <strong>ID:</strong> ${emp.employee_id}, <strong>ФИО:</strong> ${emp.last_name} ${emp.first_name} ${emp.middle_name || ''}<br>`;
            });

            let message = `<strong>Невозможно удалить должность "${position.name}"!</strong>
      <br><br>
      <strong>На этой должности работают следующие сотрудники:</strong>
      <br><br>
      ${employeeList}
      <br>
      <em>Сначала переведите этих сотрудников на другие должности.</em>
    `;
            this.modalService.showAlert('Ошибка удаления', message)
                .pipe(untilDestroyed(this)).subscribe();
            return;
        }

        const message = `Вы уверены, что хотите удалить должность <strong>"${position.name}"</strong>?`;

        this.modalService.showConfirm('Удаление должности', message, 'Удалить', 'Отмена')
            .pipe(untilDestroyed(this)).subscribe(confirmed => {
                if (confirmed) {
                    this.employeeService.deletePosition(id).
                    pipe(untilDestroyed(this)).subscribe({
                        next: () => {
                            this.loadPositions();
                            this.errorMessage = '';
                        },
                        error: (error) => {
                            console.error('Error deleting position:', error);
                            this.errorMessage = error.message;
                        }
                    });
                }
            });
    }

    public logout(): void {
        this.authService.logout();
        this.router.navigate(['/employees']);
    }

    // Вспомогательные методы для работы с формами
    private markFormGroupTouched(formGroup: FormGroup): void {
        Object.keys(formGroup.controls).forEach(key => {
            const control = formGroup.get(key);
            if (control) {
                control.markAsTouched();
            }
        });
    }

    // Геттеры для удобного доступа к контролам формы
    get employeeFormControls() {
        return this.employeeForm.controls;
    }

    get positionFormControls() {
        return this.positionForm.controls;
    }

    // Проверка валидности поля
    isFieldInvalid(form: FormGroup, fieldName: string): boolean {
        const field = form.get(fieldName);
        return !!(field && field.invalid && (field.dirty || field.touched));
    }

    // Получение сообщения об ошибке для поля
    getFieldError(form: FormGroup, fieldName: string): string {
        const field = form.get(fieldName);
        if (field && field.errors && (field.dirty || field.touched)) {
            if (field.errors['required']) {
                return this.translationService.translate('VALIDATORS.REQUIRED');
            }
            if (field.errors['email']) {
                return this.translationService.translate('VALIDATORS.EMAIL');
            }
            if (field.errors['minlength']) {
                return this.translationService.translate('VALIDATORS.MINLENGTH') + field.errors['minlength'].requiredLength;
            }
            if (field.errors['maxlength']) {
                return this.translationService.translate('VALIDATORS.MAXLENGTH') + field.errors['maxlength'].requiredLength;
            }
            if (field.errors['min']) {
                return this.translationService.translate('VALIDATORS.MIN') + field.errors['min'].min;
            }
            if (field.errors['max']) {
                return this.translationService.translate('VALIDATORS.MAX') + field.errors['max'].max;
            }
            if (field.errors['phoneFormat']) {
                return this.translationService.translate('VALIDATORS.PHONEFORMAT');
            }
            if (field.errors['invalidName']) {
                return this.translationService.translate('VALIDATORS.INVALIDNAME');
            }
        }
        return '';
    }

    // Метод для форматирования телефона при вводе
    formatPhoneNumber(event: any): void {
        const input = event.target;
        let value = input.value;
        
        // Удаляем все нецифровые символы, кроме +, (, )
        value = value.replace(/[^\d+()]/g, '');
        
        // Автоматическое добавление скобок для форматов с кодом страны
        if (value.startsWith('+')) {
            if (value.startsWith('+375') && value.length > 4 && !value.includes('(')) {
                const code = value.slice(0, 4);
                const rest = value.slice(4);
                if (rest.length <= 2) {
                    value = code + '(' + rest;
                } else if (rest.length > 2 && !value.includes(')')) {
                    value = code + '(' + rest.slice(0, 2) + ')' + rest.slice(2);
                }
            } else if (value.startsWith('+7') && value.length > 2 && !value.includes('(')) {
                const code = value.slice(0, 2);
                const rest = value.slice(2);
                if (rest.length <= 3) {
                    value = code + '(' + rest;
                } else if (rest.length > 3 && !value.includes(')')) {
                    value = code + '(' + rest.slice(0, 3) + ')' + rest.slice(3);
                }
            }
        }
        
        input.value = value;
    }

}
