import { Component, OnInit } from '@angular/core';
import { EmployeeService } from '../../services/employee.service';
import { AuthService } from '../../services/auth.service';
import { TranslationService } from '../../services/translation.service';
import { IdGeneratorService } from '../../services/id-generator.service';
import { Router } from '@angular/router';
import { IEmployee, IPosition } from '../../interfaces/employee.interface';

import { ModalService } from '../../services/modal.service';

@Component({
    selector: 'app-admin-dashboard',
    templateUrl: './admin-dashboard.component.html',
    styleUrls: ['./admin-dashboard.component.scss']
})
export class AdminDashboardComponent implements OnInit {
    employees: IEmployee[] = [];
    positions: IPosition[] = [];
    selectedEmployee: IEmployee | null = null;
    isEditing = false;

    // Состояния интерфейса
    showEmployeeForm = false;
    showPositionForm = false;
    showMainInterface = true;


    newPositionName = '';
    errorMessage = '';
    isLoading = false;

    constructor(
        private employeeService: EmployeeService,
        private authService: AuthService,
        public translationService: TranslationService,
        private idGenerator: IdGeneratorService,
        private router: Router,
        private modalService: ModalService
    ) { }

    ngOnInit(): void {
        // Проверяем авторизацию при загрузке
        if (!this.authService.validateToken()) {
            this.router.navigate(['/login']);
            return;
        }

        this.loadEmployees();
        this.loadPositions();
    }

    loadEmployees(): void {
        this.isLoading = true;
        this.employeeService.getEmployees().subscribe({
            next: (employees) => {
                this.employees = employees;
                this.isLoading = false;
            },
            error: (error) => {
                console.error('Error loading employees:', error);
                this.errorMessage = error.message;
                this.isLoading = false;

                // Если ошибка авторизации, перенаправляем на логин
                if (error.message.includes('Неавторизованный')) {
                    this.router.navigate(['/login']);
                }
            }
        });
    }

    loadPositions(): void {
        this.employeeService.getPositions().subscribe({
            next: (positions) => {
                this.positions = positions;
            },
            error: (error) => {
                console.error('Error loading positions:', error);
                this.errorMessage = error.message;

                if (error.message.includes('Неавторизованный')) {
                    this.router.navigate(['/login']);
                }
            }
        });
    }

    // Управление интерфейсом
    showAddEmployeeForm(): void {
        this.showMainInterface = false;
        this.showEmployeeForm = true;
        this.showPositionForm = false;

        this.selectedEmployee = {
            first_name: '',
            last_name: '',
            middle_name: '',
            position_id: this.positions.length > 0 ? this.positions[0].id! : 0,
            address: '',
            contact_email: '',
            contact_phone: '',
            salary: 0
        };
        this.isEditing = false;
        this.errorMessage = '';
    }

    showAddPositionForm(): void {
        this.showMainInterface = false;
        this.showPositionForm = true;
        this.showEmployeeForm = false;
        this.newPositionName = '';
        this.errorMessage = '';
    }

    cancelForm(): void {
        this.showMainInterface = true;
        this.showEmployeeForm = false;
        this.showPositionForm = false;
        this.selectedEmployee = null;
        this.errorMessage = '';
    }


    createEmployee(): void {
        if (!this.authService.validateToken()) {
            this.router.navigate(['/login']);
            return;
        }

        this.selectedEmployee = {
            first_name: '',
            last_name: '',
            middle_name: '',
            position_id: this.positions.length > 0 ? this.positions[0].id! : 0,
            address: '',
            contact_email: '',
            contact_phone: '',
            salary: 0
        };
        this.isEditing = false;
        this.showEmployeeForm = true;
        this.errorMessage = '';
    }

    editEmployee(employee: IEmployee): void {
        this.selectedEmployee = { ...employee };
        this.isEditing = true;
        this.showEmployeeForm = true;
    }

    deleteEmployee(id: number): void {
        const employee = this.employees.find(emp => emp.id === id);
        if (!employee) return;

        const message = `<strong>Вы уверены, что хотите удалить сотрудника из базы данных?</strong>
    <br><br>
    <strong>ID:</strong> ${employee.employee_id}<br>
    <strong>ФИО:</strong> ${employee.last_name} ${employee.first_name} ${employee.middle_name || ''}<br>
    <strong>Должность:</strong> ${employee.position_name}
  `;

        this.modalService.showConfirm('Удаление сотрудника', message, 'Удалить', 'Отмена')
            .subscribe(confirmed => {
                if (confirmed) {
                    this.employeeService.deleteEmployee(id).subscribe({
                        next: () => {
                            this.loadEmployees();
                            this.errorMessage = '';
                        },
                        error: (error) => {
                            console.error('Error deleting employee:', error);
                            this.errorMessage = error.message;
                        }
                    });
                }
            });
    }


    saveEmployee(employee: IEmployee): void {
        if (!this.authService.validateToken()) {
            this.router.navigate(['/login']);
            return;
        }

        // Генерируем уникальный ID только для новых сотрудников
        if (!this.isEditing) {
            const generatedId = this.idGenerator.generateEmployeeId(
                employee.first_name,
                employee.last_name,
                employee.middle_name
            );
            employee.employee_id = generatedId;
            console.log('Сгенерированный ID сотрудника:', generatedId);
        }

        this.isLoading = true;

        if (this.isEditing && employee.id) {
            this.employeeService.updateEmployee(employee.id, employee).subscribe({
                next: () => {
                    this.loadEmployees();
                    this.showEmployeeForm = false;
                    this.errorMessage = '';
                    this.isLoading = false;
                    this.modalService.showAlert('Успех', 'Сотрудник успешно обновлен!').subscribe();
                },
                error: (error) => {
                    console.error('Ошибка при изменении данных сотрудника:', error);
                    this.errorMessage = error.message;
                    this.isLoading = false;
                }
            });
        } else {
            this.employeeService.createEmployee(employee).subscribe({
                next: () => {
                    this.loadEmployees();
                    this.showEmployeeForm = false;
                    this.errorMessage = '';
                    this.isLoading = false;
                    this.modalService.showAlert('Успех', 'Сотрудник успешно создан!').subscribe();
                },
                error: (error) => {
                    console.error('Ошибка создания сотрудника:', error);
                    this.errorMessage = error.message;
                    this.isLoading = false;
                }
            });
        }
    }

    createPosition(): void {
        if (!this.authService.validateToken()) {
            this.router.navigate(['/login']);
            return;
        }

        if (this.newPositionName.trim()) {
            this.isLoading = true;
            const position: IPosition = { name: this.newPositionName.trim() };

            this.employeeService.createPosition(position).subscribe({
                next: () => {
                    this.loadPositions();
                    this.newPositionName = '';
                    this.showPositionForm = false;
                    this.errorMessage = '';
                    this.isLoading = false;
                    this.modalService.showAlert('Успех', 'Должность успешно добавлена!').subscribe();
                },
                error: (error) => {
                    console.error('Ошибка создания должности:', error);
                    this.errorMessage = error.message;
                    this.isLoading = false;
                }
            });
        }
    }

    deletePosition(id: number): void {
        const position = this.positions.find(pos => pos.id === id);
        if (!position) return;

        const employeesWithThisPosition = this.employees.filter(emp => emp.position_id === id);

        if (employeesWithThisPosition.length > 0) {
            let employeeList = '';
            employeesWithThisPosition.forEach(emp => {
                employeeList += `• <strong>ID:</strong> ${emp.employee_id}, <strong>ФИО:</strong> ${emp.last_name} ${emp.first_name} ${emp.middle_name || ''}<br>`;
            });

            let message = `<strong>Невозможно удалить должность "${position.name}"!</strong>
      <br><br>
      <strong>На этой должности работают следующие сотрудники:</strong>
      <br><br>
      ${employeeList}
      <br>
      <em>Сначала переведите этих сотрудников на другие должности.</em>
    `;
            this.modalService.showAlert('Ошибка удаления', message).subscribe();
            return;
        }

        const message = `Вы уверены, что хотите удалить должность <strong>"${position.name}"</strong>?`;

        this.modalService.showConfirm('Удаление должности', message, 'Удалить', 'Отмена')
            .subscribe(confirmed => {
                if (confirmed) {
                    this.employeeService.deletePosition(id).subscribe({
                        next: () => {
                            this.loadPositions();
                            this.errorMessage = '';
                        },
                        error: (error) => {
                            console.error('Error deleting position:', error);
                            this.errorMessage = error.message;
                        }
                    });
                }
            });
    }
    logout(): void {
        this.authService.logout();
        this.router.navigate(['/employees']);
    }
}
