import { ChangeDetectionStrategy, ChangeDetectorRef, Component, OnInit } from '@angular/core';
import { TranslationService } from '../../services/translation.service';

@Component({
  selector: 'app-language-switcher',
  template: `
    <div class="language-switcher">
      <button 
        [class.active]="currentLang === 'ru'"
        (click)="switchLanguage('ru')"
        class="lang-btn">
        RU
      </button>
      <button 
        [class.active]="currentLang === 'en'"
        (click)="switchLanguage('en')"
        class="lang-btn">
        EN
      </button>
    </div>
  `,
  styleUrls: ['./language-switcher.component.scss']
})
export class LanguageSwitcherComponent implements OnInit {
  currentLang: string = 'ru';
  translationsLoaded: boolean = false;
  showDebug: boolean = true; // Ð’Ñ€ÐµÐ¼ÐµÐ½Ð½Ð¾ Ð²ÐºÐ»ÑŽÑ‡Ð¸Ð¼ Ð´Ð»Ñ Ð¾Ñ‚Ð»Ð°Ð´ÐºÐ¸

  constructor(private translationService: TranslationService, private cdRef: ChangeDetectorRef) {}

  ngOnInit(): void {
    this.currentLang = this.translationService.getCurrentLang();
    
    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÑƒ Ð¿ÐµÑ€ÐµÐ²Ð¾Ð´Ð¾Ð²
   /* setTimeout(() => {
      this.translationsLoaded = this.translationService.areTranslationsLoaded();
      console.log('ðŸŒ Translations loaded:', this.translationsLoaded);
    }, 1000);*/
  }

  public switchLanguage(lang: string): void {
    this.translationService.setLanguage(lang);
    this.currentLang = lang;
    this.cdRef.detectChanges();
    
    // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÑ‚Ñ€Ð°Ð½Ð¸Ñ†Ñƒ Ð´Ð»Ñ Ð¿Ñ€Ð¸Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ð¿ÐµÑ€ÐµÐ²Ð¾Ð´Ð¾Ð² (Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ð¾Ðµ Ñ€ÐµÑˆÐµÐ½Ð¸Ðµ)
  /*  setTimeout(() => {
      window.location.reload();
    }, 100);*/
  }
}
