import { Component, OnInit, OnDestroy } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../shared/services/auth.service';
import { TranslationService } from '../../shared/services/translation.service';
import { UntilDestroy, untilDestroyed } from '@ngneat/until-destroy';
import { Subscription } from 'rxjs';

@UntilDestroy()
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit, OnDestroy {
  loginForm: FormGroup;
  errorMessage = '';
  isLoading = false;
  translationsLoaded = false;

  private translationsSubscription: Subscription;

  constructor(
    private authService: AuthService, 
    private router: Router,
    public translationService: TranslationService
  ) {
    this.loginForm = new FormGroup({
      username: new FormControl('', [Validators.required, Validators.minLength(3)]),
      password: new FormControl('', [Validators.required, Validators.minLength(4)])
    });

    this.translationsSubscription = this.translationService.getTranslationsLoadedObservable()
      .subscribe(loaded => {
        this.translationsLoaded = loaded;
        if (loaded) {
          console.log('Translations loaded in login component');
        }
      });
  }

  public ngOnInit(): void {
    // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏–π –≤—Ö–æ–¥, –µ—Å–ª–∏ –µ—Å—Ç—å –≤–∞–ª–∏–¥–Ω–∞—è —Å–µ—Å—Å–∏—è
    if (this.authService.autoLogin()) {
      this.router.navigate(['/admin']);
      return;
    }

    setTimeout(() => {
      const usernameField = document.getElementById('username');
      if (usernameField) {
        usernameField.focus();
      }
    }, 100);
  }

  public ngOnDestroy(): void {
    if (this.translationsSubscription) {
      this.translationsSubscription.unsubscribe();
    }
  }

  get username() {
    return this.loginForm.get('username');
  }

  get password() {
    return this.loginForm.get('password');
  }

  public login(): void {
    if (this.loginForm.valid) {
      this.isLoading = true;
      this.errorMessage = '';

      const { username, password } = this.loginForm.value;

      // –ò—Å–ø–æ–ª—å–∑—É–µ–º —Å–∏–Ω—Ö—Ä–æ–Ω–Ω—ã–π –º–µ—Ç–æ–¥ –≤–º–µ—Å—Ç–æ Observable
      const result = this.authService.login(username, password);
      
      if (result.success) {
        this.isLoading = false;
        console.log('Login successful, navigating to admin');
        this.router.navigate(['/admin']);
      } else {
        this.isLoading = false;
        this.errorMessage = result.error || this.translationService.translate('LOGIN.ERROR');
        console.error('Login error:', result.error);
        this.loginForm.patchValue({ password: '' });
        
        // –§–æ–∫—É—Å–∏—Ä—É–µ–º—Å—è –Ω–∞ –ø–æ–ª–µ —Å –æ—à–∏–±–∫–æ–π
        setTimeout(() => {
          if (this.username?.invalid) {
            document.getElementById('username')?.focus();
          } else {
            document.getElementById('password')?.focus();
          }
        }, 100);
      }
    } else {
      this.loginForm.markAllAsTouched();
      this.errorMessage = '–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –∑–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ';
      
      // –§–æ–∫—É—Å–∏—Ä—É–µ–º—Å—è –Ω–∞ –ø–µ—Ä–≤–æ–º –Ω–µ–≤–∞–ª–∏–¥–Ω–æ–º –ø–æ–ª–µ
      setTimeout(() => {
        if (this.username?.invalid) {
          document.getElementById('username')?.focus();
        } else if (this.password?.invalid) {
          document.getElementById('password')?.focus();
        }
      }, 100);
    }
  }

  public cancel(): void {
    this.router.navigate(['/employees']);
  }

  public clearForm(): void {
    this.loginForm.reset();
    this.errorMessage = '';
    
    // –§–æ–∫—É—Å–∏—Ä—É–µ–º—Å—è –Ω–∞ –ø–æ–ª–µ username –ø–æ—Å–ª–µ –æ—á–∏—Å—Ç–∫–∏
    setTimeout(() => {
      document.getElementById('username')?.focus();
    }, 100);
  }

  // –î–µ–º–æ –≤—Ö–æ–¥ –¥–ª—è —Ç–µ—Å—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
  public demoLogin(): void {
    this.loginForm.patchValue({
      username: 'admin',
      password: 'password'
    });
    
    // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º —Ñ–æ—Ä–º—É —á–µ—Ä–µ–∑ –∫–æ—Ä–æ—Ç–∫—É—é –∑–∞–¥–µ—Ä–∂–∫—É
    setTimeout(() => {
      this.login();
    }, 300);
  }

  // –ü–æ–∫–∞–∑–∞—Ç—å/—Å–∫—Ä—ã—Ç—å –ø–∞—Ä–æ–ª—å
  public togglePasswordVisibility(): void {
    const passwordInput = document.getElementById('password') as HTMLInputElement;
    if (passwordInput) {
      passwordInput.type = passwordInput.type === 'password' ? 'text' : 'password';
    }
  }

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –Ω–∞–∂–∞—Ç–∏—è Enter
  public onKeyPress(event: KeyboardEvent): void {
    if (event.key === 'Enter' && this.loginForm.valid) {
      this.login();
    }
  }
}
